import React, { useState, useEffect } from 'react';
import { Provider, useSelector, useDispatch } from 'react-redux';
import staticReducers from 'rootRedux/rootReducer';

const remoteAppScope = 'remoteApp';

const RemoteApp = () => {
  const dispatch = useDispatch();
  const state = useSelector((state: any) => state[remoteAppScope]);

  return (
    <div style={{ marginTop: '10px' }}>
      <div>RemoteApp</div>
      <div>RemoteApp's name from the redux store : {state && state.appName}</div>
    </div>
  );
};

const RemoteAppWrapper = (props: any) => {
  const { store } = props;

  useEffect(() => {
    Object.keys(staticReducers).forEach((key) => {
      store.injectReducer(key, staticReducers[key]);
    });
  }, []);

  return (
    <Provider store={store || {}}>
      <RemoteApp />
    </Provider>
  );
};

export default RemoteAppWrapper;
